// Check browser support for modern image formats
export const detectImageFormatSupport = (): ImageFormat => {
  const webp = checkWebPSupport();
  const avif = checkAVIFSupport();

  return {
    webp,
    avif,
    original: 'jpeg',
  };
};

// Check WebP support
